const rss = $input.first().json;
const webhookData = $('Webhook').first().json.body || {};
const recentTopics = webhookData.recentTopics || [];

// Build normalized headline set for exact matching
const recentHeadlinesNormalized = new Set(
  recentTopics.map(t => t.headline.toLowerCase().trim().replace(/[^a-z0-9\s]/g, ''))
);

const items = rss?.rss?.channel?.item || [];

const articles = items.slice(0, 20).map(item => ({
  title: item.title || '',
  sourceHeadline: item.title || '', // Preserve original for callback
  link: item.link || '',
  source: 'Google News',
  pubDate: item.pubDate || ''
})).filter(a => {
  const normalizedTitle = a.title.toLowerCase().trim().replace(/[^a-z0-9\s]/g, '');

  // PRIMARY: Normalized exact headline match
  if (recentHeadlinesNormalized.has(normalizedTitle)) {
    return false;
  }

  // SECONDARY: Word overlap check (>50% = likely duplicate)
  const words = new Set(normalizedTitle.split(/\s+/).filter(w => w.length > 3));
  if (words.size >= 3) {
    for (const recent of recentTopics) {
      const recentNorm = recent.headline.toLowerCase().replace(/[^a-z0-9\s]/g, '');
      const recentWords = new Set(recentNorm.split(/\s+/).filter(w => w.length > 3));
      if (recentWords.size >= 3) {
        const overlap = [...words].filter(w => recentWords.has(w)).length;
        const similarity = overlap / Math.min(words.size, recentWords.size);
        if (similarity > 0.5) {
          return false;
        }
      }
    }
  }

  return true;
}).slice(0, 10);

// Fallback if all filtered out
if (articles.length === 0 && items.length > 0) {
  const first = items[0];
  return [{
    json: {
      title: first.title || 'European Football Update',
      sourceHeadline: first.title || 'European Football Update',
      link: first.link || '',
      source: 'Google News',
      pubDate: first.pubDate || '',
      forcedFallback: true,
      recentTopicsCount: recentTopics.length
    }
  }];
}

return articles.map(a => ({ json: { ...a, recentTopicsCount: recentTopics.length } }));

