      complianceReason: "Parse error, using fallback: " + e.message,
      anonymizedDescriptions: {},
      safeImagePrompt: "Generic football stadium celebration scene with fans cheering"
    }
  }];
}

// Collect all items (each article's compliance check result)
const items = $input.all();
const geminiApiKey = items[0]?.json?.geminiApiKey;
const callbackUrl = items[0]?.json?.callbackUrl;

// Filter to only compliant topics and sort by data-richness + brand value
const compliantTopics = items
  .map(item => item.json)
  .filter(topic => topic.compliant === true)
  .sort((a, b) => {
    // Combined score prioritizing data-rich, high-brand topics
    const scoreA = (a.dataRichness || 0) * 2 + (a.brandValue || 0) * 1.5 + (a.imageability || 0);
    const scoreB = (b.dataRichness || 0) * 2 + (b.brandValue || 0) * 1.5 + (b.imageability || 0);
    return scoreB - scoreA;
  });

