const input = $input.first().json;
const prevData = $("Parse Topic Analysis").first().json;

// Get the text response from Gemini
const text = input?.candidates?.[0]?.content?.parts?.[0]?.text || "{}";

// Strip markdown fences if present
const cleaned = text.replace(/^```[a-zA-Z]*\n?/, "").replace(/\n?```\s*$/, "");

try {
  const compliance = JSON.parse(cleaned);
  return [{
    json: {
      headline: prevData.headline,
      people: prevData.people,
      teams: prevData.teams,
      event: prevData.event,
      imageability: prevData.imageability,
      imageSuggestion: prevData.imageSuggestion,
      sourceLink: prevData.sourceLink,
      compliant: compliance.compliant !== false,
      complianceReason: compliance.reason,
      anonymizedDescriptions: compliance.anonymizedDescriptions || {},
      safeImagePrompt: compliance.safeImagePrompt,
      geminiApiKey: prevData.geminiApiKey,
      callbackUrl: prevData.callbackUrl
    }
  }];
} catch (e) {
  return [{
    json: {
      ...prevData,
      compliant: true,
      complianceReason: "Parse error, using fallback: " + e.message,
      anonymizedDescriptions: {},
      safeImagePrompt: "Generic football stadium celebration scene with fans cheering"
    }
  }];
}

// Collect all items (each article's compliance check result)
const items = $input.all();
const geminiApiKey = items[0]?.json?.geminiApiKey;
const callbackUrl = items[0]?.json?.callbackUrl;

// Filter to only compliant topics and sort by imageability
const compliantTopics = items
  .map(item => item.json)
  .filter(topic => topic.compliant === true)
  .sort((a, b) => (b.imageability || 0) - (a.imageability || 0));

// If no compliant topics, create a safe fallback
if (compliantTopics.length === 0) {
  return [{
    json: {
      selectedTopic: {
        headline: 'European Football Weekly Update',
        teams: [],
        event: 'weekly roundup',
        safeImagePrompt: 'Exciting football match scene in a packed stadium with cheering fans',
        anonymizedDescriptions: {},
        fallback: true
      },
      allTopics: items.map(i => i.json),
      geminiApiKey,
      callbackUrl
    }
  }];
}

// Select the best topic (highest imageability score)
return [{
  json: {
    selectedTopic: compliantTopics[0],
    alternativeTopics: compliantTopics.slice(1, 3),
    allTopics: items.map(i => i.json),
    geminiApiKey,
    callbackUrl
  }
}];

