} catch (e) {
  // Keep fallback
}

return [{
  json: {
    selectedTopic: selectedTopic,
    sourceLink: selectedTopic.sourceLink || "",
    anonymizedDescriptions: selectedTopic.anonymizedDescriptions || {},
    hashtags: hashtags,
    hashtagString: hashtags.join(" "),
